# Contract Address of Token we want to buy
tokenToBuy = web3.toChecksumAddress(input("Enter TokenAddress: "))      # input your token address to buy

# Setup the PancakeSwap contract
contract = web3.eth.contract(address=panRouterContractAddress, abi=panabi)

def buyToken(sender_address, sender_private, sender_amount, tokenToBuy, contract):
    balance = web3.eth.get_balance(sender_address)
    humanReadable = web3.fromWei(balance, 'ether')

    # spend = web3.toChecksumAddress("0xbb4cdb9cbd36b01bd1cbaebf2de08d9173bc095c")  #wbnb contract
    spend = web3.toChecksumAddress('0xae13d989daC2f0dEbFf460aC112a837C89BAa7cd')        # WBNB on testnet

    nonce = web3.eth.get_transaction_count(sender_address)

    pancakeswap2_txn = contract.functions.swapExactETHForTokens(
        10000000000,     # set to 0, or specify minimum amount of tokeny you want to receive - consider decimals!!!
        [spend,tokenToBuy],
        sender_address,
        (int(time.time()) + 10000)
    ).buildTransaction({
        'from': sender_address,
        'value': web3.toWei(sender_amount,'ether'),      # This is the Token(BNB) amount you want to Swap from
        'gas': 25000000,
        'gasPrice': web3.toWei('10','gwei'),
        'nonce': nonce,
    })

    signed_txn = web3.eth.account.sign_transaction(pancakeswap2_txn, private_key=sender_private)
    tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
    # print(web3.toHex(tx_token))
    return web3.toHex(tx_token)
 
# sender_address = '0xa3C4fFD5Fb5f9F4c1DA865EF2bD2B6EC65B6c235'
sender_addresses = config.publicKeys
sender_privates = config.privateKeys
sender_amounts = config.amountsBNB

tx_hashes = []

for i in range(len(sender_addresses)):
    tx_hashes.append(buyToken(sender_addresses[i], sender_privates[i], sender_amounts[i], tokenToBuy, contract))

print(tx_hashes)